import React, { useState, useEffect } from 'react';
import './login.css'
import { Link } from 'react-router-dom';
import io from 'socket.io-client';
import { useNavigate } from 'react-router-dom';


const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const socket = io('http://localhost:5000');
    socket.on('login_response', (data) => {
      console.log(data);

      if (data.status === 'error') {
        setError(data.message);
      } else {
        setError('');

        navigate('/chat');
      }
    });

    return () => {
      socket.disconnect();
    };
  }, [navigate]);

  const handleLogin = (e) => {
    e.preventDefault();


    const userData = {
      username: username,
      password: password,
    };
    console.log('Sending signup data:', userData);

    const socket = io('http://localhost:5000');
    socket.emit('signup', userData);
  };


  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <label className='title'>Sign up</label>
        <input type="text" id="username" name="username" placeholder='username' value={username} onChange={(e) => setUsername(e.target.value)}/>
        <input type="password" id="password" name="password" placeholder='password' value={password} onChange={(e) => setPassword(e.target.value)}/>


        {error && <p className="error-message">{error}</p>}

        <button className='button_login' type="submit">Log In</button>

        <Link to="/login">
          <button className='text-button'>Log in</button>
        </Link>
      </form>
    </div>
  );
}

export default Login;
