import React, { useState, useEffect } from 'react';
import './login.css'
import { Link } from 'react-router-dom';
import io from 'socket.io-client';
import { useNavigate } from 'react-router-dom';

const Login = ({ setIsAuthenticated }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoggingIn, setIsLoggingIn] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const socket = io('http://localhost:5000');

    socket.on('login_response', (data) => {
      console.log(data);

      if (data.status === 'error') {
        setError(data.message);
        setIsLoggingIn(false);
      } else {
        setError('');
        setIsAuthenticated(true);
      }
    });

    return () => {
      socket.disconnect();
    };
  }, [navigate, setIsAuthenticated]);

  const handleLogin = async (e) => {
    e.preventDefault();


    if (isLoggingIn) {
      return;
    }

    try {
      setIsLoggingIn(true);

      const socket = io('http://localhost:5000');


      const logoffPromise = new Promise((resolve) => {
        socket.emit('logoff', username, () => {

          resolve();
        });
      });

      // Odota, kunnes kirjautuminen on päätökseen saatu
      await logoffPromise;

      const userData = {
        username: username,
        password: password,
      };

      // Lähetä kirjautumisviesti ja siirry chatiin
      socket.emit('login', userData);
      navigate(`/chat/${username}`);
    } catch (error) {
      console.error('Error during login:', error);
    } finally {
      setIsLoggingIn(false);
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleLogin}>
        <label className='title'>Log in</label>
        <input type="text" id="username" name="username" placeholder='username' value={username} onChange={(e) => setUsername(e.target.value)}/>
        <input type="password" id="password" name="password" placeholder='password' value={password} onChange={(e) => setPassword(e.target.value)}/>

        {error && <p className="error-message">{error}</p>}

        <button className='button_login' type="submit">Log In</button>

        <Link to="/signup">
          <button className='text-button'>sign up</button>
        </Link>
      </form>
    </div>
  );
};

export default Login;
