import React, { useState, useEffect } from 'react';
import './chat.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSignOutAlt } from '@fortawesome/free-solid-svg-icons';
import { useNavigate, useParams } from 'react-router-dom';
import io from 'socket.io-client';

const Chat = () => {
  const { username } = useParams();
  const navigate = useNavigate();
  console.log(username);

  useEffect(() => {
    const handleBeforeUnload = () => {
      const socket = io('http://localhost:5000');
      socket.emit('logoff', username);
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [username]);

  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  const handleOverlayClick = (event) => {
    if (event.target.classList.contains('modal-overlay')) {
      handleModalClose();
    }
  };

  const handleLogout = () => {
    const socket = io('http://localhost:5000');
    socket.emit('logoff', username);
    navigate('/login');
  };

  return (
    <div>
      <nav className="navbar">
        <input placeholder='Search' className='searchInput' />
        <button className='addFriends' onClick={openModal}>+</button>
        <button className='logout' onClick={handleLogout}>
          <FontAwesomeIcon icon={faSignOutAlt} className='logoutSymbol' />
        </button>
      </nav>
      <div className='nowUsing'>
        <div className="userSquare">
          <div className='userLetter'>
            O
          </div>
        </div>
        <div className='userFont'>
          {username}
        </div>
      </div>
      <div className='usersBox'></div>
      {isModalOpen && (
        <div className="modal-overlay" onClick={handleOverlayClick}>
          <div className="modal">
            <span className="close-button" onClick={handleModalClose}>
              &times;
            </span>
            <h2></h2>
            <p></p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Chat;
